
com=window.com||{};com.marklogic=window.com.marklogic||{};com.marklogic.widgets=window.com.marklogic.widgets||{};com.marklogic.widgets.cooccurence=function(container){this.container=container;this.title="Co-occurence";this.values=null;this._refresh();};com.marklogic.widgets.cooccurence.prototype.updateValues=function(values){this.values=values;this._refresh();};com.marklogic.widgets.cooccurence.prototype._refresh=function(){if(null==this.values||undefined==typeof this.values){return;}
var str="<div class='cooccurence-title'>"+this.title+"</div>";if(undefined!=this.values["values-response"]&&undefined!=this.values["values-response"].tuple){var tuplesOriginal=this.values["values-response"].tuple;bubbleSort(tuplesOriginal,"frequency");var tuples=tuplesOriginal;for(var i=0;i<tuples.length;i++){str+="<div class='cooccurence-values'>";var t=tuples[i];for(var v=0;v<t["distinct-value"].length;v++){var val=t["distinct-value"][v];str+="<span class='cooccurence-value'>"+val["_value"]+"</span>";if(v!=t["distinct-value"].length-1){str+=", ";}}
str+=" <span class='cooccurence-count'>("+t.frequency+")</span></div>";}}
document.getElementById(this.container).innerHTML=str;};